$(".hasadnaLogo").each(function(){var ctx=this.getContext("2d");var r=this.width/2;var step=0;var edge=Math.PI/10;var base=edge*2;var innerCircle=Math.ceil(r/3);ctx.lineWidth=Math.ceil(r/12);ctx.strokeStyle="#00adef";ctx.fillStyle="#00adef";ctx.save();ctx.translate(r,r);ctx.rotate(Math.PI/2+step);ctx.beginPath();ctx.moveTo(0,0);ctx.lineTo(r,0);ctx.stroke();ctx.beginPath();with(Math){ctx.moveTo(r*cos(base),r*sin(base));ctx.lineTo(r*cos(-base),r*sin(-base))}ctx.arc(0,0,r,-base,base,false);ctx.fill();ctx.rotate(Math.PI/3);for(var i=0;i<7;i++){ctx.beginPath();ctx.moveTo(r,0);ctx.lineTo(0,0);ctx.stroke();ctx.beginPath();with(Math){ctx.moveTo(r*cos(edge),r*sin(edge));ctx.quadraticCurveTo(ceil(r*0.7),0,r*cos(-edge),r*sin(-edge));ctx.arc(0,0,r,-edge,edge,false)}ctx.fill();ctx.rotate(Math.PI/4.5)}ctx.beginPath();ctx.arc(0,0,innerCircle,0,Math.PI*2,false);ctx.fill();ctx.restore()});